create table categorias(    categoriaId     int primary key auto_increment,    categoriaNombre varchar(60));create table productoPlataforma(    productoTipoId int primary key auto_increment,    productoTipo   varchar(60));create table productoTipo(    productoId       int auto_increment primary key,    productoTipoDesc varchar(30) null);create table productos(    productoId     int primary key auto_increment,    productoNombre varchar(60) not null,    productoDescripcion varchar(200) not null,    productoPrecio float not null,    productoImagen varchar(100) not null,    categoriaId    int,    productoTipoId int,    useremail     varchar(80) not null,    foreign key (categoriaId) references productoCategoria (categoriaId),    foreign key (productoTipoId) references productoPlataforma (productoTipoId),    foreign key (useremail) references usuario(useremail));