<?phpnamespace Controllers\Admin;use Controllers\PublicController;use Views\Renderer;use Dao\Servicios\Servicios as ServiciosDao;class ListaServicios extends PublicController{    private $viewData = array();            private function getAllServicios(){        $this->viewData["servicios"] = ServiciosDao::getAllServicios();    }        public function run() :void    {        $this->getAllServicios();        Renderer::render("admin/listaservicios", $this->viewData);    }}