<?phpnamespace Controllers\Admin;use Controllers\PublicController;use Views\Renderer;use Dao\Servicios\Servicios as ServiciosDao;class ListaServicios extends PublicController{    private $viewData = array();            private function getAllServicios(){        $this->viewData["servicios"] = ServiciosDao::getAllServicios();    }        private function prepareViewData() :void    {        //obtiene todos los servicios        $this->viewData["servicios"] = ServiciosDao::getAllServicios();    }        public function run() :void    {               $this->prepareViewData();        Renderer::render("admin/listaservicios", $this->viewData);    }}