<?phpnamespace Dao\CartV2;use Dao\Table;class CartV2 extends Table{    public static function agregarProductoAlCarrito    ($usercod,     $serviceId,     $crrctd,     $crrprc,     $crrfching,     $id_session    )    {        $sql = "INSERT INTO carretilla  (usercod, serviceId, crrctd, crrprc,crrfching,id_session)         VALUES (:usercod, :serviceId, :crrctd, :crrctd,:crrfching, :id_session)";        $params = array(            "usercod" => $usercod,            "serviceId" => $serviceId,            "crrctd" => $crrctd,            "crrprc" => $crrprc,            "crrfching" => $crrfching,            "id_session" => $id_session        );        return self::executeNonQuery($sql, $params);    }    public static function obtenerIdProductoEnCarrito($id_session)    {        $sql = "SELECT servicioId FROM carretilla WHERE id_session = :id_session;";        return self::obtenerUnRegistro($sql, ["id_session" => $id_session]);    }    public static function eliminarProductoCarrito($usercod, $servicioId)    {        $sql = "DELETE FROM carretilla WHERE usercod = :usercod AND serviceId = :servicioId;";                $params = ["usercod" => $usercod,            "servicioId" => $servicioId        ];                return self::executeNonQuery($sql, $params);    }    public static function obtenerProductosEnCarrito($usercod)    {        $sql = "select ecommerce.servicios.servicioid as 'ID',                        ecommerce.servicios.servicioNombre as 'Nombre',                        ecommerce.servicios.servicioPrecio as 'Precio',                        ecommerce.servicios.servicioImagen as 'Imagen',                        ecommerce.carretilla.crrctd as 'Cantidad'                        from servicios                        inner join carretilla on servicios.servicioid = carretilla.serviceId                        where carretilla.usercod =:usercod ; ";        return self::obtenerRegistros($sql, ["usercod" => $userco