<?php namespace Dao\Servicios; use Dao\Table;class Servicios extends Table {    public static function getAllServicios() {        return self::obtenerRegistros("SELECT * FROM getAllServicios",[]);    }    /*    public static function getServicioById($id) {        return self::obtenerUnRegistro("call sp_getServiciosById(:id)", ["id" => $id]);        }    */    public static function insertarServicio(                        $servicioNombre,                         $servicioPrecio,                         $servicioCategoria,                         $servicioTipo,                         $servicioDescripcion,                        $servicioImagen,                         $servicioPlataforma,                         $servicioStock        )    {        $sqlstr = "INSERT INTO servicios (servicioNombre, servicioPrecio, categoriaId, servicioTipoId, servicioDescripcion, servicioImagen, plataformaId, stock)     VALUES (:servicioNombre, :servicioPrecio, :servicioCategoria, :servicioTipo, :servicioDescripcion, :servicioImagen, :servicioPlataforma, :servicioStock)";        $params = [        "servicioNombre" => $servicioNombre,        "servicioPrecio" => $servicioPrecio,        "servicioCategoria" => $servicioCategoria,        "servicioTipo" => $servicioTipo,        "servicioDescripcion" => $servicioDescripcion,        "servicioImagen" => $servicioImagen,        "servicioPlataforma" => $servicioPlataforma,        "servicioStock" => $servicioStock    ];        return self::executeNonQuery($sqlstr, $params);        }        public static function actualizarServicio(                        $servicioId,                        $servicioNombre,                         $servicioPrecio,                         $servicioCategoria,                         $servicioTipo,                         $servicioDescripcion,                        $servicioImagen,                         $servicioPlataforma,                         $servicioStock        )    {        $sqlstr = "UPDATE servicios     SET servicioNombre = :servicioNombre,     servicioPrecio = :servicioPrecio,     categoriaId = :servicioCategoria,     servicioTipoId = :servicioTipo,     servicioDescripcion = :servicioDescripcion,     servicioImagen = :servicioImagen,     plataformaId = :servicioPlataforma,     stock = :servicioStock         WHERE servicioId = :servicioId";        $params = [        "servicioId" => $servicioId,        "servicioNombre" => $servicioNombre,        "servicioPrecio" => $servicioPrecio,        "servicioCategoria" => $servicioCategoria,        "servicioTipo" => $servicioTipo,        "servicioDescripcion" => $servicioDescripcion,        "servicioImagen" => $servicioImagen,        "servicioPlataforma" => $servicioPlataforma,        "servicioStock" => $servicioStock    ];        return self::executeNonQuery($sqlstr, $params);        }        public static function eliminarServicio($servicioId) {        $sqlstr = "DELETE FROM servicios WHERE servicioId = :servicioId";        return self::executeNonQuery($sqlstr, ["servicioId" => $servicioId]);    }        public static function getAllCategorias() {         return self::obtenerRegistros("SELECT * FROM productocategoria ",[]);    }        public static function getAllPlataformas() {         return self::obtenerRegistros("SELECT * FROM productoplataforma",[]);    }        public static function getAllTipoServicio() {         return self::obtenerRegistros("SELECT * FROM serviciotipo",[]);    }    public static function getPS4Products() {        return self::obtenerRegistros("SELECT * FROM getPS4Products",[]);    }   }